package edu.cmu.navlab.scenerecon.view.activities;import android.app.Activity;import android.app.Fragment;import android.os.Bundle;import edu.cmu.navlab.scenerecon.R;/** * SingleFragmentActivity is an abstract Activity designed to house one Fragment */public abstract class SingleFragmentActivity extends Activity {    /**     * createFragment returns the Fragment that the SingleFragmentActivity will     * use     */    protected abstract Fragment createFragment();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_sensor_test);        if (savedInstanceState == null) {            getFragmentManager().beginTransaction()                    .add(R.id.container, createFragment())                    .commit();        }    }}