package edu.cmu.navlab.scenerecon.view.fragments;import android.app.Fragment;import android.content.Intent;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import edu.cmu.navlab.scenerecon.R;import edu.cmu.navlab.scenerecon.view.activities.ImageCaptureActivity;import edu.cmu.navlab.scenerecon.view.activities.SceneReconPreferenceActivity;public class HomeFragment extends Fragment {    public static final String SCENE_NAME = "scene name";    public View onCreateView(LayoutInflater inflater, ViewGroup parent, Bundle savedInstanceState) {        View view = inflater.inflate(R.layout.home_fragment, parent, false);        final EditText sceneNameEditText = (EditText) view.findViewById(R.id.scene_name_EditText);        Button captureSceneButton = (Button) view.findViewById(R.id.capture_scene_Button);        captureSceneButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                String sceneName = sceneNameEditText.getText().toString();                if (sceneName.equals("")) {                    sceneName = "Scene Name can not be blank";                    Toast.makeText(getActivity(), sceneName, Toast.LENGTH_SHORT).show();                    return;                }                Intent i = new Intent(getActivity(), ImageCaptureActivity.class);                i.putExtra(SCENE_NAME, sceneName);                startActivity(i);            }        });        return view;    }}