package edu.cmu.navlab.scenerecon.view.fragments;import android.app.Fragment;import android.content.Intent;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import edu.cmu.navlab.scenerecon.R;import edu.cmu.navlab.scenerecon.view.activities.CompassTestActivity;import edu.cmu.navlab.scenerecon.view.activities.HelloOpenCvActivity;import edu.cmu.navlab.scenerecon.view.activities.PathTestActivity;import edu.cmu.navlab.scenerecon.view.activities.SceneReconCameraActivity;public class SetupFragment extends Fragment {    public static final String SCENE_NAME = "scene name";    private EditText mSceneNameEditText;    private Button mCaptureSceneButton;    private Button mOpenCvHelloWorldButton;    private Button mTestCompassButton;    private Button mTestPathFinderButton;    public View onCreateView(LayoutInflater inflater, ViewGroup parent, Bundle savedInstanceState) {        View view = inflater.inflate(R.layout.fragment_setup, parent, false);        mSceneNameEditText = (EditText) view.findViewById(R.id.scene_name_edittext);        mCaptureSceneButton = (Button) view.findViewById(R.id.capture_scene_button);        mCaptureSceneButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                String sceneName = mSceneNameEditText.getText().toString();                if (sceneName.equals("")) {                    sceneName = "Scene Name can not be blank";                    Toast.makeText(getActivity(), sceneName, Toast.LENGTH_SHORT).show();                    return;                }                Intent i = new Intent(getActivity(), SceneReconCameraActivity.class);                i.putExtra(SCENE_NAME, sceneName);                startActivity(i);            }        });        mOpenCvHelloWorldButton = (Button) view.findViewById(R.id.open_cv_hello_world_button);        mOpenCvHelloWorldButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent startHelloWorldActivity = new Intent(getActivity(), HelloOpenCvActivity.class);                startActivity(startHelloWorldActivity);            }        });        mTestCompassButton = (Button) view.findViewById(R.id.test_compass_button);        mTestCompassButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent startHelloWorldActivity = new Intent(getActivity(),                        CompassTestActivity.class);                startActivity(startHelloWorldActivity);            }        });        mTestPathFinderButton = (Button) view.findViewById(R.id.test_path_button);        mTestPathFinderButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent pathTestActivity = new Intent(getActivity(),                        PathTestActivity.class);                startActivity(pathTestActivity);            }        });        return view;    }}